    private void btnBuscaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscaActionPerformed
        DefaultTableModel model = (DefaultTableModel) tableClientes.getModel();
        model.setRowCount(0); // Limpa a tabela antes de fazer a busca
        
        ClienteDAO cliente = new ClienteDAO();
        ArrayList<Cliente> listaCliente = cliente.getClientesFiltro(inputBusca.getText()); // Busca clientes
        for (int i = 0; i < listaCliente.size(); i++) {
            Object[] rowData = new Object[3];
            rowData[0] = listaCliente.get(i).getId();
            rowData[1] = listaCliente.get(i).getNome();
            rowData[2] = listaCliente.get(i).getEmail();
            model.addRow(rowData); // Adiciona os dados em cada linha do model
        }
        tableClientes.setModel(model); // atribui o model para tabela
    }//GEN-LAST:event_btnBuscaActionPerformed

