    private void btnEditarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditarActionPerformed
        // TODO add your handling code here:
        // Obtém a linha selecionada
        int linhaSelecionada = tableClientes.getSelectedRow();
        
        if (linhaSelecionada != -1) {
            // Obtém o valor da célula da coluna "Nome" da linha selecionada
            String idSelcionado = tableClientes.getValueAt(linhaSelecionada, 0).toString();
            String nomeSelecionado = tableClientes.getValueAt(linhaSelecionada, 1).toString();
            String emailSelecionado = tableClientes.getValueAt(linhaSelecionada, 2).toString();
            ClienteForm form = new ClienteForm(Integer.parseInt(idSelcionado),nomeSelecionado,emailSelecionado);
            form.setDefaultCloseOperation(JDialog.HIDE_ON_CLOSE);
            form.setTitle("Cadastro de Clientes");
            form.setLocationRelativeTo(null);
            form.setResizable(false);
            form.setVisible(true);
            
        } else {
            JOptionPane.showMessageDialog(this, "Selecione uma linha para editar.");
        }
    }//GEN-LAST:event_btnEditarActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        // TODO add your handling code here:
        int linhaSelecionada = tableClientes.getSelectedRow();

        if (linhaSelecionada != -1) {
            // Confirma a exclusão
            int confirmar = JOptionPane.showConfirmDialog(this, "Tem certeza que deseja excluir "+tableClientes.getValueAt(linhaSelecionada, 1).toString()+"?");
            if (confirmar == JOptionPane.YES_OPTION) {
                ClienteDAO clid = new ClienteDAO();
                clid.remover(Integer.parseInt(tableClientes.getValueAt(linhaSelecionada, 0).toString()));
                DefaultTableModel model = (DefaultTableModel) tableClientes.getModel();
                model.removeRow(linhaSelecionada);
            }
        } else {
            JOptionPane.showMessageDialog(this, "Selecione uma linha para excluir.");
        }
    }//GEN-LAST:event_btnExcluirActionPerformed

